const layoutProps = {
  _frontmatter
};
const MDXLayout = DefaultLayout
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h1 {...{
      "id": "countdown"
    }}>{`Countdown`}</h1>
    <p>{`Countdown is used to ensure that a "countdown" to a specified end time is
displayed in a consistent format. No text styling is applied to Countdown; it is
only concerned with the text content that will be presented.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`import { Countdown } from "@jobber/components/Countdown";
`}</code></pre>
    <Playground __position={0} __code={'() => {\n  const inADayOrSo = useMemo(\n    () => new Date(new Date().getTime() + 25 * 3600 * 1000),\n    [],\n  )\n  return <Countdown date={inADayOrSo} granularity={\'dhms\'} showUnits={true} />\n}'} __scope={{
      props,
      DefaultLayout,
      Playground,
      Props,
      Countdown,
      useMemo,
      useState,
      Heading,
      Content,
      Emphasis,
      Text,
      Button,
      DefaultLayout,
      _frontmatter
