import DefaultLayout from "/Users/taylornoj/workspace/atlantis/node_modules/gatsby-theme-docz/src/base/Layout.js"
import { Playground, Props } from "docz";
import { useState } from "react";
import { Chip, Chips, ChipDismissible } from ".";
import { useFakeOptionQuery } from "./utils/mdxUtils.ts";
import { Avatar } from "@jobber/components/Avatar";
import { Content } from "@jobber/components/Content";
import { Disclosure } from "@jobber/components/Disclosure";
import { Heading } from "@jobber/components/Heading";
import { Icon } from "@jobber/components/Icon";
import { Text } from "@jobber/components/Text";
export const _frontmatter = {};

const layoutProps = {
  _frontmatter
};
const MDXLayout = DefaultLayout
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h1 {...{
      "id": "chips"
    }}>{`Chips`}</h1>
    <p>{`Chips are a flexible component that can be used for selection of items from a
group while preserving vertical space in the interface.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ts"
      }}>{`import { Chip, Chips } from "@jobber/components/Chips";
`}</code></pre>
    <h2 {...{
      "id": "usage-guidelines"
    }}>{`Usage guidelines`}</h2>
    <p>{`The `}<inlineCode parentName="p">{`<Chip>`}</inlineCode>{` will allow users to make selections in scenarios where vertical
space is at a premium. It has three high-level usages: single-select,
multi-select, and add/dismiss selection.`}</p>
    <h3 {...{
      "id": "single-select"
    }}>{`Single-select`}</h3>
    <p>{`If you need the user to make a selection of a single item from amongst several
items, and those items all have short (1–2 word) labels, a single-select Chips
will allow the user to choose one of those items.`}</p>
    <p>{`This preserves vertical space while allowing the user to clearly identify which
item they have selected.`}</p>
    <p>{`Unlike `}<a parentName="p" {...{
        "href": "/components/radio-group"
      }}>{`Radio`}</a>{`, the selected single-select Chip can be
de-selected by the user, leaving all selections blank.`}</p>
    <Playground __position={0} __code={'() => {\n  const [selected, setSelected] = useState()\n  return (\n    <Content>\n      <Text>\n        You are <u>{selected ? selected : \'_______\'}</u>\n      </Text>\n      <Chips selected={selected} onChange={setSelected}>\n        <Chip\n          prefix={<Avatar initials=\"AZ\" />}\n          label=\"Amazing\"\n          value=\"Amazing\"\n        />\n        <Chip\n          prefix={<Icon name=\"video\" />}\n          label=\"Wonderful\"\n          value=\"Wonderful\"\n        />\n        <Chip\n          prefix={<Icon name=\"starFill\" />}\n          label=\"Brilliant\"\n          value=\"Brilliant\"\n        />\n        <Chip label=\"Magnificent\" value=\"Magnificent\" />\n      </Chips>\n    </Content>\n  )\n}'} __scope={{
      props,
      DefaultLayout,
      Playground,
      Props,
      useState,
      Chip,
      Chips,
      ChipDismissible,
      useFakeOptionQuery,
      Avatar,
      Content,
      Disclosure,
      Heading,
      Icon,
      Text,
      DefaultLayout,
      _frontmatter
    }} mdxType="Playground">
  {() => {
        const [selected, setSelected] = useState();
        return <Content mdxType="Content">
        <Text mdxType="Text">
          You are <u>{selected ? selected : "_______"}</u>
        </Text>
        <Chips selected={selected} onChange={setSelected} mdxType="Chips">
          <Chip prefix={<Avatar initials="AZ" mdxType="Avatar" />} label="Amazing" value="Amazing" mdxType="Chip" />
          <Chip prefix={<Icon name="video" mdxType="Icon" />} label="Wonderful" value="Wonderful" mdxType="Chip" />
          <Chip prefix={<Icon name="starFill" mdxType="Icon" />} label="Brilliant" value="Brilliant" mdxType="Chip" />
          <Chip label="Magnificent" value="Magnificent" mdxType="Chip" />
        </Chips>
      </Content>;
      }}
    </Playground>
    <h3 {...{
      "id": "multi-select"
    }}>{`Multi-select`}</h3>
    <p>{`If you need the user to make a selection of one `}<em parentName="p">{`or more`}</em>{` items from amongst
several items, and those items all have short (1–2 word) labels, a multi-select
Chips will allow the user to choose as many items from the group as they wish.`}</p>
    <p>{`This preserves vertical space while allowing the user to clearly identify which
items they have selected. To signify to the user that multiple selections are
possible, a checkmark icon is present to reinforce the conceptual similarity to
a `}<a parentName="p" {...{
        "href": "/components/checkbox"
      }}>{`Checkbox`}</a>{`.`}</p>
    <p>{`Similar to Checkbox, a selected multi-select Chip can be de-selected by the
user, leaving all selections blank.`}</p>
    <Playground __position={1} __code={'() => {\n  const [selected, setSelected] = useState([])\n  return (\n    <Content>\n      <Text>\n        You are <u>{selected.length ? selected.join(\' \') : \'_______\'}</u>\n      </Text>\n      <Chips type=\"multiselect\" selected={selected} onChange={setSelected}>\n        <Chip\n          prefix={<Avatar initials=\"AZ\" />}\n          label=\"Amazing\"\n          value=\"Amazing\"\n        />\n        <Chip\n          prefix={<Icon name=\"video\" />}\n          label=\"Wonderful\"\n          value=\"Wonderful\"\n        />\n        <Chip\n          prefix={<Icon name=\"starFill\" />}\n          label=\"Brilliant\"\n          value=\"Brilliant\"\n        />\n        <Chip label=\"Magnificent\" value=\"Magnificent\" />\n      </Chips>\n    </Content>\n  )\n}'} __scope={{
      props,
      DefaultLayout,
      Playground,
      Props,
      useState,
      Chip,
      Chips,
      ChipDismissible,
      useFakeOptionQuery,
      Avatar,
      Content,
      Disclosure,
      Heading,
      Icon,
      Text,
      DefaultLayout,
      _frontmatter
    }} mdxType="Playground">
  {() => {
        const [selected, setSelected] = useState([]);
        return <Content mdxType="Content">
        <Text mdxType="Text">
          You are <u>{selected.length ? selected.join(" ") : "_______"}</u>
        </Text>
        <Chips type="multiselect" selected={selected} onChange={setSelected} mdxType="Chips">
          <Chip prefix={<Avatar initials="AZ" mdxType="Avatar" />} label="Amazing" value="Amazing" mdxType="Chip" />
          <Chip prefix={<Icon name="video" mdxType="Icon" />} label="Wonderful" value="Wonderful" mdxType="Chip" />
          <Chip prefix={<Icon name="starFill" mdxType="Icon" />} label="Brilliant" value="Brilliant" mdxType="Chip" />
          <Chip label="Magnificent" value="Magnificent" mdxType="Chip" />
        </Chips>
      </Content>;
      }}
    </Playground>
    <h3 {...{
      "id": "adddismiss-selection"
    }}>{`Add/dismiss selection`}</h3>
    <p>{`When the user will be selecting one or more items by inputting their own Chip
options with, use the dismissible Chip.`}</p>
    <p>{`The dismissible Chip allows them to remove previous selections from the Chips.
Use this option when the full list of possible selections is too great to
reasonably display in one group of Chip. For example, "all of my phone contacts"
would be far too many Chip options to present in one group, and would be
overwhelming for the user to interpret.`}</p>
    <p>{`This format also allows the user to create their own "freeform" Chip options,
which they can also remove once added.`}</p>
    <Playground __position={2} __code={'() => {\n  const {\n    selected,\n    options,\n    loading,\n    handleLoadMore,\n    handleSearch,\n    handleSelect,\n    handleCustomAdd,\n  } = useFakeOptionQuery()\n  return (\n    <Chips\n      type=\"dismissible\"\n      selected={selected}\n      onChange={handleSelect}\n      onCustomAdd={handleCustomAdd}\n      isLoadingMore={loading}\n      onSearch={handleSearch}\n      onLoadMore={handleLoadMore}\n    >\n      {options.map(name => (\n        <Chip key={name} label={name} value={name} />\n      ))}\n    </Chips>\n  )\n}'} __scope={{
      props,
      DefaultLayout,
      Playground,
      Props,
      useState,
      Chip,
      Chips,
      ChipDismissible,
      useFakeOptionQuery,
      Avatar,
      Content,
      Disclosure,
      Heading,
      Icon,
      Text,
      DefaultLayout,
      _frontmatter
    }} mdxType="Playground">
  {() => {
        const {
          selected,
          options,
          loading,
          handleLoadMore,
          handleSearch,
          handleSelect,
          handleCustomAdd
        } = useFakeOptionQuery();
        return <Chips type="dismissible" selected={selected} onChange={handleSelect} onCustomAdd={handleCustomAdd} isLoadingMore={loading} onSearch={handleSearch} onLoadMore={handleLoadMore} mdxType="Chips">
        {options.map(name => <Chip key={name} label={name} value={name} mdxType="Chip" />)}
      </Chips>;
      }}
    </Playground>
    <h2 {...{
      "id": "props-of-chips"
    }}>{`Props of Chips`}</h2>
    <Props of={Chips} mdxType="Props" />
    <h2 {...{
      "id": "props-of-chip"
    }}>{`Props of Chip`}</h2>
    <Props of={Chip} mdxType="Props" />
    <h2 {...{
      "id": "content-guidelines"
    }}>{`Content guidelines`}</h2>
    <p>{`Chip labels should be 1–2 words at most. If any of the options in the group may
have longer labels, consider Checkbox or Radio as necessary for your selection
type.`}</p>
    <h2 {...{
      "id": "accessibility"
    }}>{`Accessibility`}</h2>
    <p>{`Chips should convey to the user whether it is a "checkbox" or "radio" element
based on single or multi-select. The Chips in this group have the appropriate
roles and keyboard operation to allow the user to interact as though they are
dealing with a checkbox or radio button.`}</p>
    <p>{`If Chips is set for add/dimiss selections, the dismiss button will notify the
user that they will "dismiss {label name}" upon press.`}</p>
    <h2 {...{
      "id": "responsiveness"
    }}>{`Responsiveness`}</h2>
    <p>{`The Chips itself will take up as much space as its' container allows, and the
Chips will flow left-to-right. Chips may re-flow into new rows, or scroll out of
view in a single row, depending on your use case.`}</p>
    <h2 {...{
      "id": "related-components"
    }}>{`Related components`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/components/radio-group"
        }}>{`Radio`}</a>{` should be used to allow the user to select
"one-of-many" items (single-select) and the labels for the items are longer
than 1 or 2 words.`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/components/checkbox"
        }}>{`Checkbox`}</a>{` should be used to allow the user to select
"one-or-more-of-many" items (multi-select) and the labels for the items are
longer than 1 or 2 words.`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "/components/inline-label"
        }}>{`InlineLabel`}</a>{` should be used when you just need a
rectangular element that displays the status of another element.`}</li>
    </ul>
    <h2 {...{
      "id": "notes"
    }}>{`Notes`}</h2>
    <p>{`There was some consideration to bring the ability to "accent" Chips with certain
colors for parity with Jobber's mobile platform, but we will not be implementing
this for the web components as of this first iteration of our web Chip. The
mobile use-case is very specific to a filtering use case that we as of now do
not anticipate will be required for web.`}</p>

    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
